def get_file(url):
    print("Getting " + url.split("/")[-1])
    with urllib.request.urlopen(url) as response, open(url.split("/")[-1], 'wb') as file:
        shutil.copyfileobj(response, file)
    print("Done")

for url in DKC3urls:
    soup = BeautifulSoup(requests.get(url).text, 'html.parser')
    dlurl = soup.find('a', text="Click here to download")["href"]

    get_file(dlurl)
